mapper.insert:
INSERT 
INTO PPOG_CITIES PC( 
    PC.ID
    , PC.NAME
    , PC.DISTRICT_ID
    , PC.POPULATION
    , PC.DEL_FLG
) 
VALUES ( 
    #{id,jdbcType=NUMERIC}
    , #{name,jdbcType=NVARCHAR}
    , #{districtId,jdbcType=NUMERIC}
    , #{population,jdbcType=NUMERIC}
    , #{delFlg,jdbcType=NVARCHAR}
)

mapper.select:
SELECT
    COUNT(1) 
FROM
    PPOG_CITIES PC 
WHERE
    PC.DEL_FLG = 'approved' 
    AND ( 
        PC.ID LIKE #{keyword,jdbcType=NUMERIC} 
        OR PC.NAME LIKE #{keyword,jdbcType=NVARCHAR}
    )
