mapper.select:
SELECT
    COUNT(1) 
FROM
    PPOG_DISTRICTS PD 
    LEFT JOIN PPOG_CITIES PC 
        ON PC.ID = PD.SHUTO_ID 
WHERE
    PD.DEL_FLG = 'approved'
    AND ( 
        PD.NAME LIKE #{keyword,jdbcType=NVARCHAR} 
        OR PC.NAME LIKE #{keyword,jdbcType=NVARCHAR} 
        OR PD.CHIHO LIKE #{keyword,jdbcType=NVARCHAR}
    )
